mapper.select:
SELECT person.id FROM b_person person
WHERE 1=1

	AND person.phone = #{phone}


	AND person.card_num = #{idCard}


	AND person.name = #{name}

SELECT yp.id, yp.name , yp.phone , yp.card_num , yp.login_time create_date , (SELECT borrow.borr_status FROM b_borrow_list borrow WHERE borrow.id=(SELECT MAX(id) id FROM b_borrow_list borrowlist WHERE borrowlist.per_id = yp.id)) AS borrow_status, (SELECT borrow.prod_type FROM b_borrow_list borrow WHERE borrow.id=(SELECT MAX(id) id FROM b_borrow_list borrowlist WHERE borrowlist.per_id = yp.id)) AS prod_type, "" AS node_status, "" AS description, "" AS node_code, yp.source AS source_name FROM b_person yp
and yp.phone = #{phone}
and yp.name = #{name}
and yp.card_num = #{card_num}
and yp.id in
'${pId}'
and yp.create_date >= DATE_FORMAT(#{create_date_start},'%Y-%m-%d')
and yp.create_date < DATE_FORMAT(#{create_date_end},'%Y-%m-%d')
AND (SELECT borrow.borr_status FROM b_borrow_list borrow WHERE borrow.id=(SELECT MAX(id) id FROM b_borrow_list borrowlist WHERE borrowlist.per_id = yp.id)) = #{borrowStatusValue}
and yp.source = #{source_name}
ORDER BY yp.create_date desc
order by yp.create_date
desc asc
