mapper.select:
select * from ( select '' as id, p.id as perId, p.name as userName, p.card_num as cardNum, p.phone as phone, p.balance as balance, p.bank_name as bankName, p.bank_card as bankNum, '待确认' as statusName, -1 as status, null as createDate from b_person p where p.balance>0 UNION ALL select rr.id as id, p.id as perId, p.name as userName, p.card_num as cardNum, p.phone as phone, rr.amount as balance, rr.bank_name as bankName, rr.bank_num as bankNum, CASE WHEN rr.status='1' THEN '已确认' WHEN rr.status='2' THEN '退款中' WHEN rr.status='3' THEN '退款失败' ELSE '' END as statusName, rr.status as status, rr.create_date as createDate from b_refund_review rr left join b_person p on rr.per_id=p.id where rr.status in (1,2,3) ) b_refund_review where 1=1
and userName=#{userNameEq}
and cardNum=#{cardNumEq}
and phone=#{phoneEq}
and balance=#{balanceEq}
and balance > #{balanceStart}
and balance >=#{balanceStartswith}
and balance < #{balanceEnd}
and balance <= #{balanceEndswith}
and bankName=#{bankNameEq}
and status=#{statusEq}
and status !=#{statusNotEq}
and bankNum=#{bankNumEq}
and createDate > DATE_FORMAT(#{createDateStart},'%Y-%m-%d')
and createDate >= DATE_FORMAT(#{createDateStartswith},'%Y-%m-%d')
and createDate < DATE_FORMAT(#{createDateEnd},'%Y-%m-%d')
and createDate <= DATE_FORMAT(#{createDateEndswith},'%Y-%m-%d')
ORDER BY createDate DESC
order by userName order by cardNum order by phone order by balance order by bankName order by statusName order by status order by createDate order by bankNum
desc asc

select count(0) from ( select p.id as id, '' as perId, p.name as userName, p.card_num as cardNum, p.phone as phone, p.balance as balance, p.bank_name as bankName, p.bank_card as bankNum, '待确认' as statusName, -1 as status, null as createDate from b_person p where p.balance>0 UNION ALL select rr.id as id, p.id as perId, p.name as userName, p.card_num as cardNum, p.phone as phone, rr.amount as balance, rr.bank_name as bankName, rr.bank_num as bankNum, CASE WHEN rr.status='1' THEN '已确认' WHEN rr.status='2' THEN '退款中' WHEN rr.status='3' THEN '退款失败' ELSE '' END as statusName, rr.status as status, rr.create_date as createDate from b_refund_review rr left join b_person p on rr.per_id=p.id where rr.status in (1,2,3) ) refund where 1=1
and userName=#{userNameEq}
and cardNum=#{cardNumEq}
and phone=#{phoneEq}
and balance=#{balanceEq}
and bankName=#{bankNameEq}
and status=#{statusEq}
and status !=#{statusNotEq}
and bankNum=#{bankNumEq}
and createDate > DATE_FORMAT(#{createDateStart},'%Y-%m-%d')
and createDate >= DATE_FORMAT(#{createDateStartswith},'%Y-%m-%d')
and createDate < DATE_FORMAT(#{createDateEnd},'%Y-%m-%d')
and createDate <= DATE_FORMAT(#{createDateEndswith},'%Y-%m-%d')
