mapper.select:
SELECT bl.id, p.id perId, bl.borr_num borrNum, pt.id productId, pt.product_name productName, p.name, p.card_num cardNum, p.phone, bl.borr_amount borrAmount, p.bank_name bankName, p.bank_card bankCard, bl.borr_status borrStatus, bl.borr_up_status borrUpStatus, bl.contact_num contactNum, IFNULL(bl.baikelu_status,0) baikeluStatus, re.reason, re.update_date auditDate, bl.is_manual isManual, res.employee_name employeeName, bl.makeborr_date makeborrDate, bl.white_list whiteList
FROM b_borrow_list bl LEFT JOIN c_product pt ON bl.prod_id = pt.id LEFT JOIN b_person p ON bl.per_id = p.id LEFT JOIN b_review re ON bl.id = re.borr_id AND review_type = 1 LEFT JOIN c_reviewers res ON res.employ_num = re.employ_num
and bl.borr_num = #{borrNum}
and p.name = #{name}
and pt.id = #{productName}
and p.card_num = #{cardNum}
and p.phone = #{phone}
bl.borr_amount = #{borrAmount}
and p.bank_name = #{bankName}
and p.bank_card = #{bankCard}
and re.employ_num = #{employNum}
and bl.makeborr_date >= DATE_FORMAT(#{makeborrDate_start},'%Y-%m-%d')
and bl.makeborr_date < DATE_FORMAT(#{makeborrDate_end},'%Y-%m-%d')
and bl.borr_up_status = #{borrUpStatusValue}
and bl.borr_status = #{borrStatusValue}
and res.employee_name = #{employeeName}
and bl.baikelu_status = #{baikeluStatus}
and bl.baikelu_status is null
and bl.borr_status in('BS018', 'BS012', 'BS019') and bl.borr_status in('BS002','BS003','BS004','BS007','BS009','BS011','BS012','BS015','BS016','BS017','BS018', 'BS019')
and bl.is_manual <> 4
and bl.is_manual = 4
order by bl.borr_status order by bl.borr_up_status order by bl.baikelu_status order by bl.is_manual order by bl.makeborr_date order by bl.contact_num order by re.update_date
desc asc

SELECT bl.id, p.id perId, bl.borr_num borrNum, p.name, p.card_num cardNum, p.phone, pt.id productId, pt.product_name productName, bl.contact_num contactNum, bl.borr_amount borrAmount, p.bank_name bankName, p.bank_card bankCard, bl.borr_status borrStatus, bl.borr_up_status borrUpStatus, bl.baikelu_status baikeluStatus, res.employee_name employee_name, bl.makeborr_date makeborrDate, p.create_date createDate, re.reason, re.update_date auditTime
FROM b_borrow_list bl LEFT JOIN c_product pt ON bl.prod_id = pt.id LEFT JOIN b_person p ON bl.per_id = p.id LEFT JOIN b_review re ON bl.id = re.borr_id AND review_type = 1 LEFT JOIN c_reviewers res ON res.employ_num = re.employ_num
and bl.borr_num = #{borrNum}
and p.name = #{name}
and p.card_num = #{cardNum}
and pt.id = #{productName}
and bl.borr_amount = #{borrAmount}
and bl.contact_num = #{contactNum}
and p.phone = #{phone}
and p.bank_name = #{bankName}
and p.bank_card = #{bankCard}
and re.employ_num = #{employNum}
and bl.makeborr_date >= DATE_FORMAT(#{makeborrDate_start},'%Y-%m-%d')
and bl.makeborr_date < DATE_FORMAT(#{makeborrDate_end},'%Y-%m-%d')
and p.create_date >= DATE_FORMAT(#{createDate_start},'%Y-%m-%d')
and p.create_date < DATE_FORMAT(#{createDate_end},'%Y-%m-%d')
and re.update_date >= DATE_FORMAT(#{auditTime_start},'%Y-%m-%d')
and re.update_date < DATE_FORMAT(#{auditTime_end},'%Y-%m-%d')
and bl.borr_up_status = #{borrUpStatusValue}
and bl.borr_status = #{borrStatusValue}
and bl.borr_status not in('BS001')
and res.employee_name = #{employee_name}
and bl.is_manual <> 4
and bl.is_manual not in (4)
order by p.create_date order by bl.contact_num order by bl.borr_status order by bl.borr_up_status order by bl.baikelu_status order by bl.is_manual order by bl.makeborr_date order by re.update_date
desc asc

SELECT cancelNum, rejectNum, passNum, (cancelNum + rejectNum + passNum) sumNum, IFNULL(FORMAT((passNum / (passNum + rejectNum)) * 100,2),0) passRate, employName FROM ( SELECT COUNT(bl.borr_status = 'BS007' OR NULL) AS cancelNum, COUNT(bl.borr_status = 'BS008' OR NULL) AS rejectNum, (COUNT(bl.borr_status = 'BS002' OR NULL) + COUNT(bl.borr_status = 'BS003' OR NULL) + COUNT(bl.borr_status = 'BS004' OR NULL) + COUNT(bl.borr_status = 'BS005' OR NULL) + COUNT(bl.borr_status = 'BS006' OR NULL) + COUNT(bl.borr_status = 'BS010' OR NULL) + COUNT(bl.borr_status = 'BS014' OR NULL) + COUNT(bl.borr_status = 'BS011' OR NULL) + COUNT(bl.borr_status = 'BS012' OR NULL) ) AS passNum, rw.employee_name employName FROM b_borrow_list bl,review yr,Reviewers rw WHERE yr.borr_id = bl.id AND yr.review_type = 1 AND rw.employ_num = yr.employ_num
AND yr.update_date BETWEEN #{beginDate} and #{endDate}
AND yr.employ_num = #{employName,jdbcType=VARCHAR}
GROUP BY yr.employ_num) t1
order by cancelNum order by rejectNum order by passNum order by sumNum order by passRate
desc asc

mapper.update:
UPDATE b_review


        status = #{status,jdbcType=VARCHAR},


        reason = #{reason,jdbcType=VARCHAR},


        employ_num = #{employNum,jdbcType=VARCHAR}


WHERE borr_id=#{borrId,jdbcType=INTEGER}
